import { defineStore } from "pinia";

/*
Classes
*/
export class Food {
  id;
  name;
  icon;
  description;
  meals;
  protein;
  fat;
  carbs;
  mult;
  selected;
}

export class PlanInfo {
  created;
  protein;
  fat;
  carbs;
  days;
  calculationMethod;
  calculationError;
}

/*
Store
*/
export const mainStore = defineStore("mainStore", {
  state: () => {
    return {
      allFoods: [],
      planFoods: [],
      planInfo: null,
      day: 0,
    };
  },

  getters: {
    todaysFoods() {
      return this.planFoods[this.day];
    },

    todaysQuantities() {
      const total = { protein: 0, fat: 0, carbs: 0 };
      for (const food of this.planData[this.today]) {
        total.protein += food.protein * (food.amount || 0);
        total.fat += food.fat * (food.amount || 0);
        total.carbs += food.carbs * (food.amount || 0);
      }
      return total;
    },
  },

  actions: {},
});
