import { defineStore } from "pinia";
import { Calculator } from "./calculator";

/*
Classes
*/
export class Food {
  id;
  name;
  icon;
  description;
  protein;
  fat;
  carbs;
  amount;
  meal;
  category;
  selected;
}

export class PlanInfo {
  created;
  protein;
  fat;
  carbs;
  days;
  calculationMethod;
  calculationError;
}

const CalculationMethods = {
  all: "ALL",
  protein: "PROTEIN",
  fat: "FAT",
  carbs: "CARBS",
};

export const Meals = {
  breakfast: "breakfast",
  snack: "snack",
  lunch: "lunch",
  dinner: "dinner",
};

const defaultPlanInfo = {
  protein: 110,
  fat: 105,
  carbs: 100,
  days: 1,
  calculationMethod: CalculationMethods.all,
};

/*
Store
*/
export const mainStore = defineStore("mainStore", {
  state: () => {
    return {
      allFoods: [],
      planFoods: [],
      planInfo: null,
      day: 0,
      editMode: true,
      selectedQuantity: "calories",
    };
  },

  getters: {
    todaysFoods() {
      return this.planFoods[this.day];
    },

    todayIsFirstDay() {
      return this.day === 0;
    },

    todayIsLastDay() {
      return this.day === this.planInfo.days - 1;
    },

    todaysQuantities() {
      const total = { protein: 0, fat: 0, carbs: 0 };
      for (const food of this.todaysFoods) {
        total.protein += food.protein * (food.amount || 0);
        total.fat += food.fat * (food.amount || 0);
        total.carbs += food.carbs * (food.amount || 0);
      }
      return total;
    },

    allPlanQuantities() {
      const total = { protein: 0, fat: 0, carbs: 0 };
      for (let day = 0; day < this.planFoods.length; day++) {
        for (const food of this.planFoods[day]) {
          total.protein += food.protein * (food.amount || 0);
          total.fat += food.fat * (food.amount || 0);
          total.carbs += food.carbs * (food.amount || 0);
        }
      }
      return total;
    },

    calculator() {
      const calculator = new Calculator();
      calculator.allFoods = this.allFoods;
      calculator.protein = this.planInfo.protein;
      calculator.fat = this.planInfo.fat;
      calculator.carbs = this.planInfo.carbs;
      calculator.calculationMethod = this.planInfo.calculationMethod;
      calculator.calculationError = this.planInfo.calculationError;
      return calculator;
    },
  },

  actions: {
    save() {
      localStorage.setItem("planFoods", JSON.stringify(this.planFoods));

      if (this.planInfo !== null) {
        localStorage.setItem("planInfo", JSON.stringify(this.planInfo));
      }
    },

    load() {
      const planInfo = localStorage.getItem("planInfo");
      if (planInfo) {
        this.planInfo = JSON.parse(planInfo);
      } else {
        this.planInfo = { ...defaultPlanInfo, created: new Date().getTime() };
      }

      const planFoods = localStorage.getItem("planFoods");
      if (planFoods) {
        this.planFoods = JSON.parse(planFoods);
        if (planFoods.length === this.planInfo.days) {
          this.planFoods = undefined;
        }
      }

      if (!planFoods) {
        this.planFoods = [];
        for (let i = 0; i < this.planInfo.days; i++) {
          this.planFoods.push([]);
        }
      }
    },

    async fetchData() {
      const response = await fetch("/data.json");
      this.allFoods = await response.json();
    },

    goToPreviousDay() {
      if (!this.todayIsFirstDay) {
        this.day--;
      }
    },

    goToNextDay() {
      if (!this.todayIsLastDay) {
        this.day++;
      }
    },

    toggleEditMode() {
      this.editMode = !this.editMode;
    },

    recalculateToday() {
      const foods = this.calculator.calculateDay();
      this.planFoods[this.day] = foods;
      this.save();
    },

    changeFoodSelected(food) {
      food.selected = !food.selected;
      this.save();
    },

    changeFoodAmount(food) {
      const nextAmountMap = {
        0.25: 0.5,
        0.5: 0.75,
        0.75: 1,
        1: 1.25,
        1.25: 1.5,
        1.5: 0.25,
      };
      food.amount = nextAmountMap[food.amount] || 1;
    },
  },
});
