import { defineStore } from "pinia";

/*
Classes
*/
export class Food {
  id;
  name;
  icon;
  description;
  protein;
  fat;
  carbs;
  mult;
}

export class PlanInfo {
  created;
  protein;
  fat;
  carbs;
  days;
  calculationMethod;
  calculationError;
}

/*
Store
*/
export const mainStore = defineStore("mainStore", {
  state: () => {
    return {
      allFoodItems: [],
      planFoodItems: Food,
      planInfo: null,
      day: 0,
    };
  },
});
