/*
 * Helper classes and enums
 */

export const CalculationMethods = {
  all: "ALL",
  protein: "PROTEIN",
  fat: "FAT",
  carbs: "CARBS",
  calories: "CALORIES",
};

/*
 * Calculator class
 */

export class Calculator {
  allFoods;
  targetProtein;
  targetFat;
  targetCarbs;
  calculationMethod;
  calculationError;

  //
  static STARTING_POPULATION = 100;

  constructor() {}

  get targetCalories() {
    return this.targetProtein * 4 + this.targetCarbs * 4 + this.targetFat * 9;
  }

  getErrorFunction() {
    switch (this.calculationMethod) {
      case CalculationMethods.all:
        return (food) => {
          const e1 = food.protein - this.targetProtein;
          const e2 = food.fat - this.targetFat;
          const e3 = food.carbs - this.targetCarbs;
          return e1 ** 2 + e2 ** 2 + e3 ** 2;
        };
      case CalculationMethods.protein:
        return (food) => (food.protein - this.targetProtein) ** 2;
      case CalculationMethods.fat:
        return (food) => (food.fat - this.targetFat) ** 2;
      case CalculationMethods.carbs:
        return (food) => (food.carbs - this.targetCarbs) ** 2;
      case CalculationMethods.calories:
        return (food) => {
          const calories = food.protein * 4 + food.carbs * 4 + food.fat * 9;
          return (calories - this.targetCalories) ** 2;
        };
    }
  }

  calculateError(plan) {
    const errFunc = this.getErrorFunction();
    return plan.flat().reduce((sum, food) => sum + errFunc(food), 0);
  }

  createRandomPopulation() {
    // Get foods per meal
    const foodsPerMeal = [];
    const meals = ["b", "s", "l", "d"];
    for (const meal of meals) {
      foodsPerMeal.push(this.allFoods.filter((food) => food[meal]));
    }

    // Build random plans
    const plans = [];
    const foodCounts = [1, 2, 3, 4, 5];
    for (let i = 0; i < this.STARTING_POPULATION; i++) {
      const plan = [];
      for (let m = 0; m < meals.length; m++) {
        const foodCount = Math.floor(Math.random() * foodCounts.length);
        const foods = getRandomSubsample(foodsPerMeal[m], foodCount);
        plan.push(foods.map((food) => ({ ...food })));
      }
      plans.push(plan);
    }

    return plans;
  }

  extinsionAndCrossover(plans) {
    // Order plans by error
    plans = plans.sort(
      (a, b) => this.calculateError(b) - this.calculateError(a)
    );

    // Remove the ones with high error
    const survivalRate = 0.1;
    plans = plans.slice(0, Math.floor(survivalRate * plans.length));
  }

  calc() {
    // 1. Create a random population
    //
    // 2. Cross breed
  }

  calculateDay() {
    const Meals = ["breakfast", "snack", "lunch", "dinner"];
    const day = [];

    Meals.forEach(() => {
      const subsample = getRandomSubsample(this.allFoods, 3);
      for (const item of subsample) {
        item.amount = 1;
      }
      day.push([...subsample]);
    });

    return day;
  }

  calculateDays(days) {
    console.log(days);
  }
}

/*
 * Helper function
 */

function getRandomSubsample(originalList, sampleSize) {
  if (sampleSize <= 0 || sampleSize > originalList.length) {
    console.error("Invalid sample size");
    return [];
  }

  // Shuffle the original list using Fisher-Yates algorithm
  const shuffledList = [...originalList];
  for (let i = shuffledList.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [shuffledList[i], shuffledList[j]] = [shuffledList[j], shuffledList[i]];
  }

  // Take the first 'sampleSize' elements from the shuffled list
  const subsampledList = shuffledList.slice(0, sampleSize);

  return subsampledList;
}
