/*
 * Helper classes and enums
 */

export const CalculationMethods = {
  all: "ALL",
  protein: "PROTEIN",
  fat: "FAT",
  carbs: "CARBS",
  calories: "CALORIES",
};

/*
 * Calculator class
 */

export class Calculator {
  allFoods;
  targetProtein;
  targetFat;
  targetCarbs;
  calculationMethod;
  calculationError;

  //
  STARTING_POPULATION = 100;
  SURVIVAL_RATE = 0.1;
  MAX_ITERATIONS = 10;

  constructor() {}

  get targetCalories() {
    return this.targetProtein * 4 + this.targetCarbs * 4 + this.targetFat * 9;
  }

  calculateError(plan) {
    const total = { protein: 0, fat: 0, carbs: 0 };
    for (const food of plan.flat()) {
      total.protein += food.protein * (food.amount || 0);
      total.fat += food.fat * (food.amount || 0);
      total.carbs += food.carbs * (food.amount || 0);
    }

    if (this.calculationMethod === CalculationMethods.all) {
      const e1 = total.protein - this.targetProtein;
      const e2 = total.fat - this.targetFat;
      const e3 = total.carbs - this.targetCarbs;
      return e1 ** 2 + e2 ** 2 + e3 ** 2;
    } else if (this.calculationMethod === CalculationMethods.protein) {
      return (total.protein - this.targetProtein) ** 2;
    } else if (this.calculationMethod === CalculationMethods.fat) {
      return (total.fat - this.targetFat) ** 2;
    } else if (this.calculationMethod === CalculationMethods.carbs) {
      return (total.carbs - this.targetCarbs) ** 2;
    } else if (this.calculationMethod === CalculationMethods.calories) {
      const calories = total.protein * 4 + total.fat * 9 + total.carbs * 4;
      return (calories - this.targetCalories) ** 2;
    }
  }

  createRandomPopulation() {
    // Get foods per meal
    const foodsPerMeal = [];
    const meals = ["b", "s", "l", "d"];
    for (const meal of meals) {
      foodsPerMeal.push(this.allFoods.filter((food) => food[meal]));
    }

    // Build random plans
    const plans = [];
    const foodCounts = [1, 2, 3, 4, 5];
    for (let i = 0; i < this.STARTING_POPULATION; i++) {
      const plan = [];
      for (let m = 0; m < meals.length; m++) {
        const foodCount = 1 + Math.floor(Math.random() * foodCounts.length);
        const foods = getRandomSubsample(foodsPerMeal[m], foodCount);
        plan.push(foods.map((food) => ({ ...food })));
      }
      plans.push(plan);
    }

    return plans;
  }

  extinctionAndCrossover(plans) {
    // Order plans by error
    plans = plans.sort(
      (a, b) => this.calculateError(b) - this.calculateError(a)
    );

    // Remove the ones with high error
    const survivalRate = 0.1;
    const survivorsCount = Math.ceil(survivalRate * plans.length);
    console.log("survivors", survivorsCount);
    plans = plans.slice(0, survivorsCount);

    // Crossover
    const newPlans = [];
    newPlans.push(...plans);

    return newPlans;
  }

  calculateDay() {
    let plans = this.createRandomPopulation();
    for (let i = 0; i < this.MAX_ITERATIONS; i++) {
      plans = this.extinctionAndCrossover(plans);
    }
    return plans;
  }

  calculateDays(days) {
    console.log(days);
  }
}

/*
 * Helper function
 */

function getRandomSubsample(originalList, sampleSize) {
  if (sampleSize <= 0 || sampleSize > originalList.length) {
    console.error("Invalid sample size");
    return [];
  }

  // Shuffle the original list using Fisher-Yates algorithm
  const shuffledList = [...originalList];
  for (let i = shuffledList.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [shuffledList[i], shuffledList[j]] = [shuffledList[j], shuffledList[i]];
  }

  // Take the first 'sampleSize' elements from the shuffled list
  const subsampledList = shuffledList.slice(0, sampleSize);

  return subsampledList;
}
